import { peliculas } from '../../../../../Data'
import '../../../CSS/Read.css'
import { useNavigate } from 'react-router-dom'
import { useState } from 'react'
import Card from '../../../../ConfirmationCard/Card'
import { deleteMovie } from '../../../../../DataFunctions'

export default function ReadMovies(){

    const navigate = useNavigate()
    const [confirm, setConfirm] = useState(false)
    const [movieId, setMovieId] = useState(0)

    const handleEdit = (movieId) => {
        navigate(`/movies/${movieId}`)
    }

    const handleDelete = (movieId) => {
        setMovieId(movieId)
        setConfirm(true)
    }

    const handleConfirm = () => {
        deleteMovie(movieId)
        setConfirm(false)
    }

    const handleCancel = () => {
        setConfirm(false)
        alert('Delete canceled')
    }

    return(
        <div>
            <h1>Movies</h1>
            {peliculas && peliculas.length >0 ? 
                <table>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Nombre</th>
                            <th>Genero</th>
                            <th>Duracion</th>
                            <th>Inventario</th>
                        </tr>
                    </thead>
                    <tbody>
                        {peliculas.map((pelicula) => {
                            return(
                                <tr key={pelicula.idPelicula}>
                                    <td>{pelicula.idPelicula}</td>
                                    <td>{pelicula.nombre}</td>
                                    <td>{pelicula.genero}</td>
                                    <td>{pelicula.duracion}</td>
                                    <td>{pelicula.inventario}</td>
                                    <td>
                                        <button onClick={() => handleEdit(pelicula.idPelicula)}>Edit</button>
                                        <button onClick={() => handleDelete(pelicula.idPelicula)}>Delete</button>
                                    </td>
                                </tr>
                            ) }
                        )}
                    </tbody>
                </table> : <h2>No hay peliculas</h2>
            }
            {confirm && <Card message='Are you sure you want to delete this movie?' handleConfirm={handleConfirm} handleCancel={handleCancel}/>}
        </div>
    )
}
